  $scope.save = function(modalstate, id){
    var _url = $scope._token['url']+ $scope.subUrl;
    if($.isEmptyObject($scope._token)) return false;
    // $scope.dataForm['alamat1']      = $scope.dataForm['alamat1'].toString();
    // $scope.dataForm['alamat_npwp']  = $scope.dataForm['alamat_npwp'].toString();
    // $scope.dataForm['keterangan']   = $scope.dataForm['keterangan'].toString();

    $http({
      'method'  : ($scope.dataForm['kode_anggaran']) ? 'PUT' : 'POST',
      'url'     : _url + (($scope.dataForm['kode_anggaran']) ? '/'+$scope._token['token'] : ''),
      'data'    : $.param($scope.dataForm),
      'headers' : {'Content-Type': 'application/x-www-form-urlencoded'}
    }).success(function(response){
      if(modalstate === 'edit') {
          new PNotify({
              title : 'Berhasil',
              text  : 'Edit Data Berhasil',
              type  : 'success'
          });
      }
      else {
          new PNotify({
              title : 'Berhasil',
              text  : 'Input Data Berhasil',
              type  : 'success'
          });
      }
      showData(response);
      $scope.modalstate = 'edit'; 

      $.gritter.add({
            title: 'Application',
            text: 'Data Berhasil Disimpan',
            fade_out_speed: 5000
        });
    }).error(function(error){
      //console.log(error);
    });
  }
