app.controller('lovCoaController', function($scope, $http, $uibModalInstance, parameter){//, API_URL) {
	$scope.libraryTempCoa = {};
  $scope.totalItemsTempCoa = {};
  $scope.totalItemsCoa = 0;
  $scope.subUrlCoa = '/acc/coas';
  $scope._token = {};
  $scope._dataCoa = {};

  $scope.parameter = parameter;
  $scope.dataModal = {}

  // console.log($scope.parameter);
  // $scope.dataModal['f_customer'] = $scope.parameter['_f_customer'];

    //retrieve employees listing from API
  $http({
    'method'  : 'GET',
    'url'   : '/token' 
  }).success(function(response){
    // console.log(response);
    $scope._token     = response;

    getResultsPageCoa(1);

  }).error(function(response){
      console.log(response);
  });

  $scope.searchDBCoa = function(){
      if($scope.searchTextCoa.length >= 1){
        // console.log($scope.libraryTempCoa);
        if($.isEmptyObject($scope.libraryTempCoa)){
              $scope.libraryTempCoa = $scope._dataCoa;
              $scope.totalItemsTempCoa = $scope.totalItemsCoa;
              $scope._dataCoa = {};
        }
        getResultsPageCoa(1);
      }else{
        // console.log('masuk');
          if(!$.isEmptyObject($scope.libraryTempCoa)){
              $scope._dataCoa = $scope.libraryTempCoa ;
              $scope.totalItemsCoa = $scope.totalItemsTempCoa;
              $scope.libraryTempCoa = {};
          }
      }
  }

  $scope.pageChangedCoa = function(newPage) {
    getResultsPageCoa(newPage);
  };

  $scope.cancel = function () {
    $uibModalInstance.dismiss('cancel');
  };

  $scope.lovCoaSelected = function(_element){
    // console.log(_element);
    $uibModalInstance.close(_element);
  }

/*

*/
  function getResultsPageCoa(pageNumber) {
      var _url = $scope._token['url']+ $scope.subUrlCoa;

      if(! $.isEmptyObject($scope.libraryTempCoa)){
        $http({
          'method'  : 'GET',
          'url'     : _url+'?search='+$scope.searchTextCoa+'&page='+pageNumber 
        }).success(function(response){
          // console.log(response);
          $scope._dataCoa      = response.data;
          $scope.totalItemsCoa = response.total;
          $scope.tableCoa      = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
            console.log(response);
        });
      }else{
        $http({
          'method'  : 'GET',
          'url'   : _url+'?page='+pageNumber//+'&f_customer='+$scope.parameter['_f_customer'] 
        }).success(function(response){
          // console.log($scope._dataCoa);
          $scope._dataCoa        = response.data;
          $scope.totalItemsCoa   = response.total;
          $scope.tableCoa        = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
          console.log(response);
        });
      }
  }

}); 
