app.controller('lovFakturPiutangController', function($scope, $http, $uibModalInstance, parameter){//, API_URL) {
	$scope.libraryTempFakturPiutang = {};
  $scope.totalItemsTempFakturPiutang = {};
  $scope.totalItemsFakturPiutang = 0;
  $scope.subUrlFakturPiutang = '/acc/lovFakturPiutang';
  $scope._token = {};
  $scope._dataFakturPiutang = {};

  $scope.parameter = parameter;
  $scope.dataModal = {}

  // console.log($scope.parameter);
  // $scope.dataModal['f_customer'] = $scope.parameter['_f_customer'];

    //retrieve employees listing from API
  $http({
    'method'  : 'GET',
    'url'   : '/token' 
  }).success(function(response){
    // console.log(response);
    $scope._token     = response;

    getResultsPageFakturPiutang(1);

  }).error(function(response){
      console.log(response);
  });

  $scope.searchDBFakturPiutang = function(){
      if($scope.searchTextFakturPiutang.length >= 1){
        // console.log($scope.libraryTempFakturPiutang);
        if($.isEmptyObject($scope.libraryTempFakturPiutang)){
              $scope.libraryTempFakturPiutang = $scope._dataFakturPiutang;
              $scope.totalItemsTempFakturPiutang = $scope.totalItemsFakturPiutang;
              $scope._dataFakturPiutang = {};
        }
        getResultsPageFakturPiutang(1);
      }else{
        // console.log('masuk');
          if(!$.isEmptyObject($scope.libraryTempFakturPiutang)){
              $scope._dataFakturPiutang = $scope.libraryTempFakturPiutang ;
              $scope.totalItemsFakturPiutang = $scope.totalItemsTempFakturPiutang;
              $scope.libraryTempFakturPiutang = {};
          }
      }
  }

  $scope.pageChangedFakturPiutang = function(newPage) {
    getResultsPageFakturPiutang(newPage);
  };

  $scope.cancel = function () {
    $uibModalInstance.dismiss('cancel');
  };

  $scope.lovFakturPiutangSelected = function(_element){
    // console.log(_element);
    $uibModalInstance.close(_element);
  }

/*

*/
  function getResultsPageFakturPiutang(pageNumber) {
      var _url = $scope._token['url']+ $scope.subUrlFakturPiutang;

      if(! $.isEmptyObject($scope.libraryTempFakturPiutang)){
        $http({
          'method'  : 'GET',
          'url'     : _url+'?search='+$scope.searchTextFakturPiutang+'&page='+pageNumber+'&coa_id='+$scope.parameter['_coa_id']  
        }).success(function(response){
          // console.log(response);
          $scope._dataFakturPiutang      = response.data;
          $scope.totalItemsFakturPiutang = response.total;
          $scope.tableFakturPiutang      = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
            console.log(response);
        });
      }else{
        $http({
          'method'  : 'GET',
          'url'   : _url+'?page='+pageNumber+'&coa_id='+$scope.parameter['_coa_id'] 
        }).success(function(response){
          // console.log($scope._dataFakturPiutang);
          $scope._dataFakturPiutang        = response.data;
          $scope.totalItemsFakturPiutang   = response.total;
          $scope.tableFakturPiutang        = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
          console.log(response);
        });
      }
  }

}); 
