app.controller('lovAccCoaController', function($scope, $http, $uibModalInstance, parameter){//, API_URL) {
	$scope.libraryTempAccCoa = {};
  $scope.totalItemsTempAccCoa = {};
  $scope.totalItemsAccCoa = 0;
  $scope.subUrlAccCoa = '/keu/accCoa';
  $scope._token = {};
  $scope._dataAccCoa = {};

  $scope.parameter = parameter;
  $scope.dataModal = {}

  // console.log($scope.parameter);
  // $scope.dataModal['f_customer'] = $scope.parameter['_f_customer'];

    //retrieve employees listing from API
  $http({
    'method'  : 'GET',
    'url'   : '/token' 
  }).success(function(response){
    // console.log(response);
    $scope._token     = response;

    getResultsPageAccCoa(1);

  }).error(function(response){
      console.log(response);
  });

  $scope.searchDBAccCoa = function(){
      if($scope.searchTextAccCoa.length >= 1){
        // console.log($scope.libraryTempAccCoa);
        if($.isEmptyObject($scope.libraryTempAccCoa)){
              $scope.libraryTempAccCoa = $scope._dataAccCoa;
              $scope.totalItemsTempAccCoa = $scope.totalItemsAccCoa;
              $scope._dataAccCoa = {};
        }
        getResultsPageAccCoa(1);
      }else{
        // console.log('masuk');
          if(!$.isEmptyObject($scope.libraryTempAccCoa)){
              $scope._dataAccCoa = $scope.libraryTempAccCoa ;
              $scope.totalItemsAccCoa = $scope.totalItemsTempAccCoa;
              $scope.libraryTempAccCoa = {};
          }
      }
  }

  $scope.pageChangedAccCoa = function(newPage) {
    getResultsPageAccCoa(newPage);
  };

  $scope.cancel = function () {
    $uibModalInstance.dismiss('cancel');
  };

  $scope.lovAccCoaSelected = function(_element){
    //console.log(_element);
    $uibModalInstance.close(_element);
  }

  // $scope.getFileAccCoa = function(_name){
  //   var _url = $scope._token['url']+ '/hkm/AccCoa';
  //   if($.isEmptyObject($scope._token)) return false;
  //   window.open(_url+'/files/AccCoa'+'/'+_name, '_blank'); 
  // }

/*

*/
  function getResultsPageAccCoa(pageNumber) {
      var _url = $scope._token['url']+ $scope.subUrlAccCoa;
      //alert(_url);
      if(! $.isEmptyObject($scope.libraryTempAccCoa)){
        $http({
          'method'  : 'GET',
          'url'     : _url+'?search='+$scope.searchTextAccCoa+'&page='+pageNumber 
        }).success(function(response){
          //console.log(response);
          $scope._dataAccCoa      = response.data;
          $scope.totalItemsAccCoa = response.total;
          $scope.tableAccCoa      = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
            console.log(response);
        });
      }else{
        $http({
          'method'  : 'GET',
          'url'   : _url+'?page='+pageNumber//+'&f_customer='+$scope.parameter['_f_customer'] 
        }).success(function(response){
          //console.log(response.data);
          $scope._dataAccCoa        = response.data;
          $scope.totalItemsAccCoa   = response.total;
          $scope.tableAccCoa        = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
          console.log(response);
        });
      }
  }

}); 
