app.controller('lovApCstController', function($scope, $http, $uibModalInstance, parameter){//, API_URL) {
	$scope.libraryTempApCst = {};
  $scope.totalItemsTempApCst = {};
  $scope.totalItemsApCst = 0;
  $scope.subUrlApCst = '/acc/coas/ap/cst';
  $scope._token = {};
  $scope._dataApCst = {};

  $scope.parameter = parameter;
  $scope.dataModal = {}

  // console.log($scope.parameter);
  // $scope.dataModal['f_customer'] = $scope.parameter['_f_customer'];

    //retrieve employees listing from API
  $http({
    'method'  : 'GET',
    'url'   : '/token' 
  }).success(function(response){
    // console.log(response);
    $scope._token     = response;

    getResultsPageApCst(1);

  }).error(function(response){
      console.log(response);
  });

  $scope.searchDBApCst = function(){
      if($scope.searchTextApCst.length >= 1){
        // console.log($scope.libraryTempApCst);
        if($.isEmptyObject($scope.libraryTempApCst)){
              $scope.libraryTempApCst = $scope._dataApCst;
              $scope.totalItemsTempApCst = $scope.totalItemsApCst;
              $scope._dataApCst = {};
        }
        getResultsPageApCst(1);
      }else{
        // console.log('masuk');
          if(!$.isEmptyObject($scope.libraryTempApCst)){
              $scope._dataApCst = $scope.libraryTempApCst ;
              $scope.totalItemsApCst = $scope.totalItemsTempApCst;
              $scope.libraryTempApCst = {};
          }
      }
  }

  $scope.pageChangedApCst = function(newPage) {
    getResultsPageApCst(newPage);
  };

  $scope.cancel = function () {
    $uibModalInstance.dismiss('cancel');
  };

  $scope.lovApCstSelected = function(_element){
    //console.log(_element);
    $uibModalInstance.close(_element);
  }

  // $scope.getFileApCst = function(_name){
  //   var _url = $scope._token['url']+ '/hkm/ApCst';
  //   if($.isEmptyObject($scope._token)) return false;
  //   window.open(_url+'/files/ApCst'+'/'+_name, '_blank'); 
  // }

/*

*/
  function getResultsPageApCst(pageNumber) {
      var _url = $scope._token['url']+ $scope.subUrlApCst;
      //alert(_url);
      if(! $.isEmptyObject($scope.libraryTempApCst)){
        $http({
          'method'  : 'GET',
          'url'     : _url+'?search='+$scope.searchTextApCst+'&page='+pageNumber 
        }).success(function(response){
          //console.log(response);
          $scope._dataApCst      = response.data;
          $scope.totalItemsApCst = response.total;
          $scope.tableApCst      = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
            console.log(response);
        });
      }else{
        $http({
          'method'  : 'GET',
          'url'   : _url+'?page='+pageNumber//+'&f_customer='+$scope.parameter['_f_customer'] 
        }).success(function(response){
          //console.log(response.data);
          $scope._dataApCst        = response.data;
          $scope.totalItemsApCst   = response.total;
          $scope.tableApCst        = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
          console.log(response);
        });
      }
  }

}); 
