app.controller('lovCostCenterController', function($scope, $http, $uibModalInstance, parameter){//, API_URL) {
	$scope.libraryTempCostCenter = {};
  $scope.totalItemsTempCostCenter = {};
  $scope.totalItemsCostCenter = 0;
  $scope.subUrlCostCenter = '/keu/cost_center';
  $scope._token = {};
  $scope._dataCostCenter = {};

  $scope.parameter = parameter;
  $scope.dataModal = {}

  // console.log($scope.parameter);
  // $scope.dataModal['f_customer'] = $scope.parameter['_f_customer'];

    //retrieve employees listing from API
  $http({
    'method'  : 'GET',
    'url'   : '/token' 
  }).success(function(response){
    // console.log(response);
    $scope._token     = response;

    getResultsPageCostCenter(1);

  }).error(function(response){
      console.log(response);
  });

  $scope.searchDBCostCenter = function(){
      if($scope.searchTextCostCenter.length >= 1){
        // console.log($scope.libraryTempCostCenter);
        if($.isEmptyObject($scope.libraryTempCostCenter)){
              $scope.libraryTempCostCenter = $scope._dataCostCenter;
              $scope.totalItemsTempCostCenter = $scope.totalItemsCostCenter;
              $scope._dataCostCenter = {};
        }
        getResultsPageCostCenter(1);
      }else{
        // console.log('masuk');
          if(!$.isEmptyObject($scope.libraryTempCostCenter)){
              $scope._dataCostCenter = $scope.libraryTempCostCenter ;
              $scope.totalItemsCostCenter = $scope.totalItemsTempCostCenter;
              $scope.libraryTempCostCenter = {};
          }
      }
  }

  $scope.pageChangedCostCenter = function(newPage) {
    getResultsPageCostCenter(newPage);
  };

  $scope.cancel = function () {
    $uibModalInstance.dismiss('cancel');
  };

  $scope.lovCostCenterSelected = function(_element){
    //console.log(_element);
    $uibModalInstance.close(_element);
  }

  // $scope.getFileCostCenter = function(_name){
  //   var _url = $scope._token['url']+ '/hkm/CostCenter';
  //   if($.isEmptyObject($scope._token)) return false;
  //   window.open(_url+'/files/CostCenter'+'/'+_name, '_blank'); 
  // }

/*

*/
  function getResultsPageCostCenter(pageNumber) {
      var _url = $scope._token['url']+ $scope.subUrlCostCenter;
      //alert(_url);
      if(! $.isEmptyObject($scope.libraryTempCostCenter)){
        $http({
          'method'  : 'GET',
          'url'     : _url+'?search='+$scope.searchTextCostCenter+'&page='+pageNumber 
        }).success(function(response){
          //console.log(response);
          $scope._dataCostCenter      = response.data;
          $scope.totalItemsCostCenter = response.total;
          $scope.tableCostCenter      = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
            console.log(response);
        });
      }else{
        $http({
          'method'  : 'GET',
          'url'   : _url+'?page='+pageNumber//+'&f_customer='+$scope.parameter['_f_customer'] 
        }).success(function(response){
          //console.log(response.data);
          $scope._dataCostCenter        = response.data;
          $scope.totalItemsCostCenter   = response.total;
          $scope.tableCostCenter        = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
          console.log(response);
        });
      }
  }

}); 
