app.controller('lovJnsTransController', function($scope, $http, $uibModalInstance, parameter){//, API_URL) {
	$scope.libraryTempJnsTrans = {};
  $scope.totalItemsTempJnsTrans = {};
  $scope.totalItemsJnsTrans = 0;
  $scope.subUrlJnsTrans = '/keu/jns_trans';
  $scope._token = {};
  $scope._dataJnsTrans = {};

  $scope.parameter = parameter;
  $scope.dataModal = {}

  // console.log($scope.parameter);
  // $scope.dataModal['f_customer'] = $scope.parameter['_f_customer'];

    //retrieve employees listing from API
  $http({
    'method'  : 'GET',
    'url'   : '/token' 
  }).success(function(response){
    // console.log(response);
    $scope._token     = response;

    getResultsPageJnsTrans(1);

  }).error(function(response){
      console.log(response);
  });

  $scope.searchDBJnsTrans = function(){
      if($scope.searchTextJnsTrans.length >= 1){
        // console.log($scope.libraryTempJnsTrans);
        if($.isEmptyObject($scope.libraryTempJnsTrans)){
              $scope.libraryTempJnsTrans = $scope._dataJnsTrans;
              $scope.totalItemsTempJnsTrans = $scope.totalItemsJnsTrans;
              $scope._dataJnsTrans = {};
        }
        getResultsPageJnsTrans(1);
      }else{
        // console.log('masuk');
          if(!$.isEmptyObject($scope.libraryTempJnsTrans)){
              $scope._dataJnsTrans = $scope.libraryTempJnsTrans ;
              $scope.totalItemsJnsTrans = $scope.totalItemsTempJnsTrans;
              $scope.libraryTempJnsTrans = {};
          }
      }
  }

  $scope.pageChangedJnsTrans = function(newPage) {
    getResultsPageJnsTrans(newPage);
  };

  $scope.cancel = function () {
    $uibModalInstance.dismiss('cancel');
  };

  $scope.lovJnsTransSelected = function(_element){
    // console.log(_element);
    $uibModalInstance.close(_element);
  }

  // $scope.getFileJnsTrans = function(_name){
  //   var _url = $scope._token['url']+ '/hkm/JnsTrans';
  //   if($.isEmptyObject($scope._token)) return false;
  //   window.open(_url+'/files/JnsTrans'+'/'+_name, '_blank'); 
  // }

/*

*/
  function getResultsPageJnsTrans(pageNumber) {
      var _url = $scope._token['url']+ $scope.subUrlJnsTrans;

      if(! $.isEmptyObject($scope.libraryTempJnsTrans)){
        $http({
          'method'  : 'GET',
          'url'     : _url+'?search='+$scope.searchTextJnsTrans+'&page='+pageNumber 
        }).success(function(response){
          // console.log(response);
          $scope._dataJnsTrans      = response.data;
          $scope.totalItemsJnsTrans = response.total;
          $scope.tableJnsTrans      = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
            console.log(response);
        });
      }else{
        $http({
          'method'  : 'GET',
          'url'   : _url+'?page='+pageNumber//+'&f_customer='+$scope.parameter['_f_customer'] 
        }).success(function(response){
          // console.log($scope._dataJnsTrans);
          $scope._dataJnsTrans        = response.data;
          $scope.totalItemsJnsTrans   = response.total;
          $scope.tableJnsTrans        = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
          console.log(response);
        });
      }
  }

}); 
