app.controller('lovNpbController', function($scope, $http, $uibModalInstance, parameter){//, API_URL) {
  $scope.libraryTempNpb = {};
  $scope.totalItemsTempNpb = {};
  $scope.totalItemsNpb = 0;
  $scope.subUrlNpb = '/keu/npb';
  $scope._token = {};
  $scope._dataNpb = {};

  $scope.parameter = parameter;
  $scope.dataModal = {}

  // console.log($scope.parameter);
  // $scope.dataModal['f_customer'] = $scope.parameter['_f_customer'];

    //retrieve employees listing from API
  $http({
    'method'  : 'GET',
    'url'   : '/token' 
  }).success(function(response){
    // console.log(response);
    $scope._token     = response;

    getResultsPageNpb(1);

  }).error(function(response){
      //console.log(response);
  });

  $scope.searchDBNpb = function(){
      if($scope.searchTextNpb.length >= 1){
        // console.log($scope.libraryTempNpb);
        if($.isEmptyObject($scope.libraryTempNpb)){
              $scope.libraryTempNpb = $scope._dataNpb;
              $scope.totalItemsTempNpb = $scope.totalItemsNpb;
              $scope._dataNpb = {};
        }
        getResultsPageNpb(1);
      }else{
        // console.log('masuk');
          if(!$.isEmptyObject($scope.libraryTempNpb)){
              $scope._dataNpb = $scope.libraryTempNpb ;
              $scope.totalItemsNpb = $scope.totalItemsTempNpb;
              $scope.libraryTempNpb = {};
          }
      }
  }

  $scope.pageChangedNpb = function(newPage) {
    getResultsPageNpb(newPage);
  };

  $scope.cancel = function () {
    $uibModalInstance.dismiss('cancel');
  };

  $scope.lovNpbSelected = function(_element){
    // console.log(_element);
    $uibModalInstance.close(_element);
  }

  // $scope.getFileNpb = function(_name){
  //   var _url = $scope._token['url']+ '/hkm/Npb';
  //   if($.isEmptyObject($scope._token)) return false;
  //   window.open(_url+'/files/Npb'+'/'+_name, '_blank'); 
  // }

/*

*/
  function getResultsPageNpb(pageNumber) {
      var _url = $scope._token['url']+ $scope.subUrlNpb;

      if(! $.isEmptyObject($scope.libraryTempNpb)){
        $http({
          'method'  : 'GET',
          'url'     : _url+'?search='+$scope.searchTextNpb+'&page='+pageNumber 
        }).success(function(response){
          // console.log(response);
          $scope._dataNpb      = response.data;
          $scope.totalItemsNpb = response.total;
          $scope.tableNpb      = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
            //console.log(response);
        });
      }else{
        $http({
          'method'  : 'GET',
          'url'   : _url+'?page='+pageNumber//+'&f_customer='+$scope.parameter['_f_customer'] 
        }).success(function(response){
          //console.log(response.data);
          $scope._dataNpb        = response.data;
          $scope.totalItemsNpb   = response.total;
          $scope.tableNpb        = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
          //console.log(response);
        });
      }
  }

}); 
