app.controller('lovPembayaranController', function($scope, $http, $uibModalInstance, parameter){//, API_URL) {
  $scope.libraryTempPembayaran = {};
  $scope.totalItemsTempPembayaran = {};
  $scope.totalItemsPembayaran = 0;
  $scope.subUrlPembayaran = '/keu/pembayaran';
  $scope._token = {};
  $scope._dataPembayaran = {};

  $scope.parameter = parameter;
  $scope.dataModal = {}

  // console.log($scope.parameter);
  // $scope.dataModal['f_customer'] = $scope.parameter['_f_customer'];

    //retrieve employees listing from API
  $http({
    'method'  : 'GET',
    'url'   : '/token' 
  }).success(function(response){
    // console.log(response);
    $scope._token     = response;

    getResultsPagePembayaran(1);

  }).error(function(response){
      //console.log(response);
  });

  $scope.searchDBPembayaran = function(){
      if($scope.searchTextPembayaran.length >= 1){
        // console.log($scope.libraryTempPembayaran);
        if($.isEmptyObject($scope.libraryTempPembayaran)){
              $scope.libraryTempPembayaran = $scope._dataPembayaran;
              $scope.totalItemsTempPembayaran = $scope.totalItemsPembayaran;
              $scope._dataPembayaran = {};
        }
        getResultsPagePembayaran(1);
      }else{
        // console.log('masuk');
          if(!$.isEmptyObject($scope.libraryTempPembayaran)){
              $scope._dataPembayaran = $scope.libraryTempPembayaran ;
              $scope.totalItemsPembayaran = $scope.totalItemsTempPembayaran;
              $scope.libraryTempPembayaran = {};
          }
      }
  }

  $scope.pageChangedPembayaran = function(newPage) {
    getResultsPagePembayaran(newPage);
  };

  $scope.cancel = function () {
    $uibModalInstance.dismiss('cancel');
  };

  $scope.lovPembayaranSelected = function(_element){
    // console.log(_element);
    $uibModalInstance.close(_element);
  }

  // $scope.getFilePembayaran = function(_name){
  //   var _url = $scope._token['url']+ '/hkm/Pembayaran';
  //   if($.isEmptyObject($scope._token)) return false;
  //   window.open(_url+'/files/Pembayaran'+'/'+_name, '_blank'); 
  // }

/*

*/
  function getResultsPagePembayaran(pageNumber) {
      var _url = $scope._token['url']+ $scope.subUrlPembayaran;

      if(! $.isEmptyObject($scope.libraryTempPembayaran)){
        $http({
          'method'  : 'GET',
          'url'     : _url+'?search='+$scope.searchTextPembayaran+'&page='+pageNumber 
        }).success(function(response){
          // console.log(response);
          $scope._dataPembayaran      = response.data;
          $scope.totalItemsPembayaran = response.total;
          $scope.tablePembayaran      = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
            //console.log(response);
        });
      }else{
        $http({
          'method'  : 'GET',
          'url'   : _url+'?page='+pageNumber//+'&f_customer='+$scope.parameter['_f_customer'] 
        }).success(function(response){
          //console.log(response.data);
          $scope._dataPembayaran        = response.data;
          $scope.totalItemsPembayaran   = response.total;
          $scope.tablePembayaran        = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
          //console.log(response);
        });
      }
  }

}); 
