app.controller('lovPenerimaanController', function($scope, $http, $uibModalInstance, parameter){//, API_URL) {
  $scope.libraryTempPenerimaan = {};
  $scope.totalItemsTempPenerimaan = {};
  $scope.totalItemsPenerimaan = 0;
  $scope.subUrlPenerimaan = '/keu/penerimaan';
  $scope._token = {};
  $scope._dataPenerimaan = {};

  $scope.parameter = parameter;
  $scope.dataModal = {}

  // console.log($scope.parameter);
  // $scope.dataModal['f_customer'] = $scope.parameter['_f_customer'];

    //retrieve employees listing from API
  $http({
    'method'  : 'GET',
    'url'   : '/token' 
  }).success(function(response){
    // console.log(response);
    $scope._token     = response;

    getResultsPagePenerimaan(1);

  }).error(function(response){
      //console.log(response);
  });

  $scope.searchDBPenerimaan = function(){
      if($scope.searchTextPenerimaan.length >= 1){
        // console.log($scope.libraryTempPenerimaan);
        if($.isEmptyObject($scope.libraryTempPenerimaan)){
              $scope.libraryTempPenerimaan = $scope._dataPenerimaan;
              $scope.totalItemsTempPenerimaan = $scope.totalItemsPenerimaan;
              $scope._dataPenerimaan = {};
        }
        getResultsPagePenerimaan(1);
      }else{
        // console.log('masuk');
          if(!$.isEmptyObject($scope.libraryTempPenerimaan)){
              $scope._dataPenerimaan = $scope.libraryTempPenerimaan ;
              $scope.totalItemsPenerimaan = $scope.totalItemsTempPenerimaan;
              $scope.libraryTempPenerimaan = {};
          }
      }
  }

  $scope.pageChangedPenerimaan = function(newPage) {
    getResultsPagePenerimaan(newPage);
  };

  $scope.cancel = function () {
    $uibModalInstance.dismiss('cancel');
  };

  $scope.lovPenerimaanSelected = function(_element){
    // console.log(_element);
    $uibModalInstance.close(_element);
  }

  // $scope.getFilePenerimaan = function(_name){
  //   var _url = $scope._token['url']+ '/hkm/Penerimaan';
  //   if($.isEmptyObject($scope._token)) return false;
  //   window.open(_url+'/files/Penerimaan'+'/'+_name, '_blank'); 
  // }

/*

*/
  function getResultsPagePenerimaan(pageNumber) {
      var _url = $scope._token['url']+ $scope.subUrlPenerimaan;

      if(! $.isEmptyObject($scope.libraryTempPenerimaan)){
        $http({
          'method'  : 'GET',
          'url'     : _url+'?search='+$scope.searchTextPenerimaan+'&page='+pageNumber 
        }).success(function(response){
          // console.log(response);
          $scope._dataPenerimaan      = response.data;
          $scope.totalItemsPenerimaan = response.total;
          $scope.tablePenerimaan      = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
            //console.log(response);
        });
      }else{
        $http({
          'method'  : 'GET',
          'url'   : _url+'?page='+pageNumber//+'&f_customer='+$scope.parameter['_f_customer'] 
        }).success(function(response){
          //console.log(response.data);
          $scope._dataPenerimaan        = response.data;
          $scope.totalItemsPenerimaan   = response.total;
          $scope.tablePenerimaan        = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
          //console.log(response);
        });
      }
  }

}); 
