app.controller('lovSeriController', function($scope, $http, $uibModalInstance, parameter){//, API_URL) {
	$scope.libraryTempSeri = {};
  $scope.totalItemsTempSeri = {};
  $scope.totalItemsSeri = 0;
  $scope.subUrlSeri = '/keu/seri_sb';
  $scope._token = {};
  $scope._dataSeri = {};

  $scope.parameter = parameter;
  $scope.dataModal = {}

  $http({
    'method'  : 'GET',
    'url'   : '/token' 
  }).success(function(response){
    // console.log(response);
    $scope._token     = response;

    getResultsPageSeri(1);

  }).error(function(response){
      console.log(response);
  });

  $scope.searchDBSeri = function(){
      if($scope.searchTextSeri.length >= 1){

        if($.isEmptyObject($scope.libraryTempSeri)){
              $scope.libraryTempSeri = $scope._dataSeri;
              $scope.totalItemsTempSeri = $scope.totalItemsSeri;
              $scope._dataSeri = {};
        }
        getResultsPageSeri(1);
      }else{
          if(!$.isEmptyObject($scope.libraryTempSeri)){
              $scope._dataSeri = $scope.libraryTempSeri ;
              $scope.totalItemsSeri = $scope.totalItemsTempSeri;
              $scope.libraryTempSeri = {};
          }
      }
  }

  $scope.pageChangedSeri = function(newPage) {
    getResultsPageSeri(newPage);
  };

  $scope.cancel = function () {
    $uibModalInstance.dismiss('cancel');
  };

  $scope.lovSeriSelected = function(_element){
    // console.log(_element);
    $uibModalInstance.close(_element);
  }

  // $scope.getFileSeri = function(_name){
  //   var _url = $scope._token['url']+ '/hkm/Seri';
  //   if($.isEmptyObject($scope._token)) return false;
  //   window.open(_url+'/files/Seri'+'/'+_name, '_blank'); 
  // }

/*

*/
  function getResultsPageSeri(pageNumber) {
      var _url = $scope._token['url']+ $scope.subUrlSeri;

      if(! $.isEmptyObject($scope.libraryTempSeri)){
        $http({
          'method'  : 'GET',
          'url'     : _url+'?search='+$scope.searchTextSeri+'&page='+pageNumber 
        }).success(function(response){
          // console.log(response);
          $scope._dataSeri      = response.data;
          $scope.totalItemsSeri = response.total;
          $scope.tableSeri      = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
            console.log(response);
        });
      }else{
        $http({
          'method'  : 'GET',
          'url'   : _url+'?page='+pageNumber//+'&f_customer='+$scope.parameter['_f_customer'] 
        }).success(function(response){
          // console.log($scope._dataSeri);
          $scope._dataSeri        = response.data;
          $scope.totalItemsSeri   = response.total;
          $scope.tableSeri        = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
          console.log(response);
        });
      }
  }

}); 
