app.controller('lovSetoranBankController', function($scope, $http, $uibModalInstance, parameter){//, API_URL) {
  $scope.libraryTempSetoranBank = {};
  $scope.totalItemsTempSetoranBank = {};
  $scope.totalItemsSetoranBank = 0;
  $scope.subUrlSetoranBank = '/keu/setoran_bank';
  $scope._token = {};
  $scope._dataSetoranBank = {};

  $scope.parameter = parameter;
  $scope.dataModal = {}

  // console.log($scope.parameter);
  // $scope.dataModal['f_customer'] = $scope.parameter['_f_customer'];

    //retrieve employees listing from API
  $http({
    'method'  : 'GET',
    'url'   : '/token' 
  }).success(function(response){
    // console.log(response);
    $scope._token     = response;

    getResultsPageSetoranBank(1);

  }).error(function(response){
      //console.log(response);
  });

  $scope.searchDBSetoranBank = function(){
      if($scope.searchTextSetoranBank.length >= 1){
        // console.log($scope.libraryTempSetoranBank);
        if($.isEmptyObject($scope.libraryTempSetoranBank)){
              $scope.libraryTempSetoranBank = $scope._dataSetoranBank;
              $scope.totalItemsTempSetoranBank = $scope.totalItemsSetoranBank;
              $scope._dataSetoranBank = {};
        }
        getResultsPageSetoranBank(1);
      }else{
        // console.log('masuk');
          if(!$.isEmptyObject($scope.libraryTempSetoranBank)){
              $scope._dataSetoranBank = $scope.libraryTempSetoranBank ;
              $scope.totalItemsSetoranBank = $scope.totalItemsTempSetoranBank;
              $scope.libraryTempSetoranBank = {};
          }
      }
  }

  $scope.pageChangedSetoranBank = function(newPage) {
    getResultsPageSetoranBank(newPage);
  };

  $scope.cancel = function () {
    $uibModalInstance.dismiss('cancel');
  };

  $scope.lovSetoranBankSelected = function(_element){
    // console.log(_element);
    $uibModalInstance.close(_element);
  }

  // $scope.getFileSetoranBank = function(_name){
  //   var _url = $scope._token['url']+ '/hkm/SetoranBank';
  //   if($.isEmptyObject($scope._token)) return false;
  //   window.open(_url+'/files/SetoranBank'+'/'+_name, '_blank'); 
  // }

/*

*/
  function getResultsPageSetoranBank(pageNumber) {
      var _url = $scope._token['url']+ $scope.subUrlSetoranBank;

      if(! $.isEmptyObject($scope.libraryTempSetoranBank)){
        $http({
          'method'  : 'GET',
          'url'     : _url+'?search='+$scope.searchTextSetoranBank+'&page='+pageNumber 
        }).success(function(response){
          // console.log(response);
          $scope._dataSetoranBank      = response.data;
          $scope.totalItemsSetoranBank = response.total;
          $scope.tableSetoranBank      = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
            //console.log(response);
        });
      }else{
        $http({
          'method'  : 'GET',
          'url'   : _url+'?page='+pageNumber//+'&f_customer='+$scope.parameter['_f_customer'] 
        }).success(function(response){
          //console.log(response.data);
          $scope._dataSetoranBank        = response.data;
          $scope.totalItemsSetoranBank   = response.total;
          $scope.tableSetoranBank        = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
          //console.log(response);
        });
      }
  }

}); 
