app.controller('lovFpController', function($scope, $http, $uibModalInstance, parameter){//, API_URL) {
	$scope.libraryTempFp = {};
  $scope.totalItemsTempFp = {};
  $scope.totalItemsFp = 0;
  $scope.subUrlFp = '/pms/fpMst';
  $scope._token = {};
  $scope._dataFp = {};

  $scope.parameter = parameter;
  $scope.dataModal = {}

  $http({
    'method'  : 'GET',
    'url'   : '/token' 
  }).success(function(response){
    // console.log(response);
    $scope._token     = response;

    getResultsPageFp(1);

  }).error(function(response){
      console.log(response);
  });

  $scope.searchDBFp = function(){
      if($scope.searchTextFp.length >= 1){

        if($.isEmptyObject($scope.libraryTempFp)){
              $scope.libraryTempFp = $scope._dataFp;
              $scope.totalItemsTempFp = $scope.totalItemsFp;
              $scope._dataFp = {};
        }
        getResultsPageFp(1);
      }else{
          if(!$.isEmptyObject($scope.libraryTempFp)){
              $scope._dataFp = $scope.libraryTempFp ;
              $scope.totalItemsFp = $scope.totalItemsTempFp;
              $scope.libraryTempFp = {};
          }
      }
  }

  $scope.pageChangedFp = function(newPage) {
    getResultsPageFp(newPage);
  };

  $scope.cancel = function () {
    $uibModalInstance.dismiss('cancel');
  };

  $scope.lovFpSelected = function(_element){
    // console.log(_element);
    $uibModalInstance.close(_element);
  }

  // $scope.getFileFp = function(_name){
  //   var _url = $scope._token['url']+ '/hkm/Fp';
  //   if($.isEmptyObject($scope._token)) return false;
  //   window.open(_url+'/files/Fp'+'/'+_name, '_blank'); 
  // }

/*

*/
  function getResultsPageFp(pageNumber) {
      var _url = $scope._token['url']+ $scope.subUrlFp;

      if(! $.isEmptyObject($scope.libraryTempFp)){
        $http({
          'method'  : 'GET',
          'url'     : _url+'?search='+$scope.searchTextFp+'&page='+pageNumber 
        }).success(function(response){
          // console.log(response);
          $scope._dataFp      = response.data;
          $scope.totalItemsFp = response.total;
          $scope.tableFp      = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
            console.log(response);
        });
      }else{
        $http({
          'method'  : 'GET',
          'url'   : _url+'?page='+pageNumber//+'&f_customer='+$scope.parameter['_f_customer'] 
        }).success(function(response){
          // console.log($scope._dataFp);
          $scope._dataFp        = response.data;
          $scope.totalItemsFp   = response.total;
          $scope.tableFp        = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
          console.log(response);
        });
      }
  }

}); 
