app.controller('lovAllPerusController', function($scope, $http, $uibModalInstance, parameter){//, API_URL) {
  $scope.libraryTempPerus = {};
  $scope.totalItemsTempPerus = {};
  $scope.totalItemsPerus = 0;
  $scope.subUrlPerus = '/sdm/allPerus';
  $scope._token = {};
  $scope._dataPerus = {};

  $scope.parameter = parameter;
  $scope.dataModal = {}

  $http({
    'method'  : 'GET',
    'url'   : '/token' 
  }).success(function(response){
    // console.log(response);
    $scope._token     = response;

    getResultsPagePerus(1);

  }).error(function(response){
      console.log(response);
  });

  $scope.searchDBPerus = function(){
      if($scope.searchTextPerus.length >= 1){

        if($.isEmptyObject($scope.libraryTempPerus)){
              $scope.libraryTempPerus = $scope._dataPerus;
              $scope.totalItemsTempPerus = $scope.totalItemsPerus;
              $scope._dataPerus = {};
        }
        getResultsPagePerus(1);
      }else{
          if(!$.isEmptyObject($scope.libraryTempPerus)){
              $scope._dataPerus = $scope.libraryTempPerus ;
              $scope.totalItemsPerus = $scope.totalItemsTempPerus;
              $scope.libraryTempPerus = {};
          }
      }
  }

  $scope.pageChangedPerus = function(newPage) {
    getResultsPagePerus(newPage);
  };

  $scope.cancel = function () {
    $uibModalInstance.dismiss('cancel');
  };

  $scope.lovPerusSelected = function(_element){
    // console.log(_element);
    $uibModalInstance.close(_element);
  }

  // $scope.getFilePerus = function(_name){
  //   var _url = $scope._token['url']+ '/hkm/Perus';
  //   if($.isEmptyObject($scope._token)) return false;
  //   window.open(_url+'/files/Perus'+'/'+_name, '_blank'); 
  // }

/*

*/
  function getResultsPagePerus(pageNumber) {
      var _url = $scope._token['url']+ $scope.subUrlPerus;

      if(! $.isEmptyObject($scope.libraryTempPerus)){
        $http({
          'method'  : 'GET',
          'url'     : _url+'?search='+$scope.searchTextPerus+'&page='+pageNumber 
        }).success(function(response){
          // console.log(response);
          $scope._dataPerus      = response.data;
          $scope.totalItemsPerus = response.total;
          $scope.tablePerus      = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
            console.log(response);
        });
      }else{
        $http({
          'method'  : 'GET',
          'url'   : _url+'?page='+pageNumber//+'&f_customer='+$scope.parameter['_f_customer'] 
        }).success(function(response){
          // console.log($scope._dataPerus);
          $scope._dataPerus        = response.data;
          $scope.totalItemsPerus   = response.total;
          $scope.tablePerus        = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
          console.log(response);
        });
      }
  }

}); 
