app.controller('lovPegawaiController', function($scope, $http, $uibModalInstance, parameter){//, API_URL) {
  $scope.libraryTempPegawai = {};
  $scope.totalItemsTempPegawai = {};
  $scope.totalItemsPegawai = 0;
  $scope.subUrlPegawai = '/sdm/pegawai';
  $scope._token = {};
  $scope._dataPegawai = {};

  $scope.parameter = parameter;
  $scope.dataModal = {}

  $http({
    'method'  : 'GET',
    'url'   : '/token' 
  }).success(function(response){
    // console.log(response);
    $scope._token     = response;

    getResultsPagePegawai(1);

  }).error(function(response){
      console.log(response);
  });

  $scope.searchDBPegawai = function(){
      if($scope.searchTextPegawai.length >= 1){

        if($.isEmptyObject($scope.libraryTempPegawai)){
              $scope.libraryTempPegawai = $scope._dataPegawai;
              $scope.totalItemsTempPegawai = $scope.totalItemsPegawai;
              $scope._dataPegawai = {};
        }
        getResultsPagePegawai(1);
      }else{
          if(!$.isEmptyObject($scope.libraryTempPegawai)){
              $scope._dataPegawai = $scope.libraryTempPegawai ;
              $scope.totalItemsPegawai = $scope.totalItemsTempPegawai;
              $scope.libraryTempPegawai = {};
          }
      }
  }

  $scope.pageChangedPegawai = function(newPage) {
    getResultsPagePegawai(newPage);
  };

  $scope.cancel = function () {
    $uibModalInstance.dismiss('cancel');
  };

  $scope.lovPegawaiSelected = function(_element){
    // console.log(_element);
    $uibModalInstance.close(_element);
  }

  // $scope.getFilePegawai = function(_name){
  //   var _url = $scope._token['url']+ '/hkm/Pegawai';
  //   if($.isEmptyObject($scope._token)) return false;
  //   window.open(_url+'/files/Pegawai'+'/'+_name, '_blank'); 
  // }

/*

*/
  function getResultsPagePegawai(pageNumber) {
      var _url = $scope._token['url']+ $scope.subUrlPegawai;

      if(! $.isEmptyObject($scope.libraryTempPegawai)){
        $http({
          'method'  : 'GET',
          'url'     : _url+'?search='+$scope.searchTextPegawai+'&page='+pageNumber 
        }).success(function(response){
          // console.log(response);
          $scope._dataPegawai      = response.data;
          $scope.totalItemsPegawai = response.total;
          $scope.tablePegawai      = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
            console.log(response);
        });
      }else{
        $http({
          'method'  : 'GET',
          'url'   : _url+'?page='+pageNumber//+'&f_customer='+$scope.parameter['_f_customer'] 
        }).success(function(response){
          // console.log($scope._dataPegawai);
          $scope._dataPegawai        = response.data;
          $scope.totalItemsPegawai   = response.total;
          $scope.tablePegawai        = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
          console.log(response);
        });
      }
  }

}); 
