app.controller('lovSupplierController', function($scope, $http, $uibModalInstance, parameter){//, API_URL) {
	$scope.libraryTempSupplier = {};
  $scope.totalItemsTempSupplier = {};
  $scope.totalItemsSupplier = 0;
  $scope.subUrlSupplier = '/umum/supplier';
  $scope._token = {};
  $scope._dataSupplier = {};

  $scope.parameter = parameter;
  $scope.dataModal = {}

  // console.log($scope.parameter);
  // $scope.dataModal['f_customer'] = $scope.parameter['_f_customer'];

    //retrieve employees listing from API
  $http({
    'method'  : 'GET',
    'url'   : '/token' 
  }).success(function(response){
    // console.log(response);
    $scope._token     = response;

    getResultsPageSupplier(1);

  }).error(function(response){
      console.log(response);
  });

  $scope.searchDBSupplier = function(){
      if($scope.searchTextSupplier.length >= 1){
        // console.log($scope.libraryTempSupplier);
        if($.isEmptyObject($scope.libraryTempSupplier)){
              $scope.libraryTempSupplier = $scope._dataSupplier;
              $scope.totalItemsTempSupplier = $scope.totalItemsSupplier;
              $scope._dataSupplier = {};
        }
        getResultsPageSupplier(1);
      }else{
        // console.log('masuk');
          if(!$.isEmptyObject($scope.libraryTempSupplier)){
              $scope._dataSupplier = $scope.libraryTempSupplier ;
              $scope.totalItemsSupplier = $scope.totalItemsTempSupplier;
              $scope.libraryTempSupplier = {};
          }
      }
  }

  $scope.pageChangedSupplier = function(newPage) {
    getResultsPageSupplier(newPage);
  };

  $scope.cancel = function () {
    $uibModalInstance.dismiss('cancel');
  };

  $scope.lovSupplierSelected = function(_element){
    // console.log(_element);
    $uibModalInstance.close(_element);
  }

/*

*/
  function getResultsPageSupplier(pageNumber) {
      var _url = $scope._token['url']+ $scope.subUrlSupplier;

      if(! $.isEmptyObject($scope.libraryTempSupplier)){
        $http({
          'method'  : 'GET',
          'url'     : _url+'?search='+$scope.searchTextSupplier+'&page='+pageNumber+'&coa_id='+$scope.parameter['coa_id'] 
        }).success(function(response){
          // console.log(response);
          $scope._dataSupplier      = response.data;
          $scope.totalItemsSupplier = response.total;
          $scope.tableSupplier      = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
            console.log(response);
        });
      }else{
        $http({
          'method'  : 'GET',
          'url'   : _url+'?page='+pageNumber+'&coa_id='+$scope.parameter['coa_id'] 
        }).success(function(response){
          // console.log($scope._dataSupplier);
          $scope._dataSupplier        = response.data;
          $scope.totalItemsSupplier   = response.total;
          $scope.tableSupplier        = { TotalItems : response.total, CurrentItems : response.to };
        }).error(function(response){
          console.log(response);
        });
      }
  }

}); 
